mapper.select:
SELECT BAD.*,BW.BW_USERNAME
FROM(
    SELECT * FROM BUS_ACHIEVEMENT_DETAIL


            AND ID = #{ID}


            AND BA_ID = #{BA_ID}


            AND BW_ID = #{BW_ID}


)BAD
INNER JOIN BUS_WECHAT BW ON BW.ID = BAD.BW_ID

mapper.update:
UPDATE BUS_ACHIEVEMENT_DETAIL
ID=#{ID}, BA_ID=#{BA_ID}, BW_ID=#{BW_ID}, BAD_REMARKS=#{BAD_ADDRESS}, BAD_REMARKS=#{BAD_REMARKS}, BAD_ENTERTIME=#{BAD_ENTERTIME},
ID=#{ID}

mapper.select:
SELECT COUNT(*)
FROM V_ACHIEVEMENT_DETAIL


        AND BW_ID = #{BW_ID}

SELECT *
FROM V_ACHIEVEMENT_DETAIL


        ${whereClause}



    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

SELECT
BMIA.*,
BA.BA_NAME
FROM(
SELECT * FROM BUS_MAIN_IMAGE_ACHIEVEMENT


        AND ID = #{ID}


        AND BMI_ID = #{BMI_ID}


        AND BMI_ID IN(${BMI_ID_LIST})


)BMIA
INNER JOIN BUS_ACHIEVEMENT BA ON BA.ID = BMIA.BA_ID

mapper.update:
UPDATE BUS_MAIN_IMAGE_ACHIEVEMENT
ID=#{ID}, BA_ID=#{BA_ID}, BMI_ID=#{BMI_ID},
ID=#{ID}

mapper.delete:
		DELETE FROM BUS_MAIN_IMAGE_ACHIEVEMENT


                AND ID=#{ID}


                AND BA_ID=#{BA_ID}


                AND BMI_ID=#{BMI_ID}
