mapper.select:
SELECT * FROM BUS_ACHIEVEMENT


        AND ID = #{ID}


        AND IS_STATUS = #{IS_STATUS}

mapper.update:
UPDATE BUS_ACHIEVEMENT
ID=#{ID}, BA_NAME=#{BA_NAME}, BA_LONGITUDE=#{BA_LONGITUDE}, BA_LATITUDE=#{BA_LATITUDE}, BA_ENTRYTIME=#{BA_ENTRYTIME}, BA_RANGE=#{BA_RANGE}, BA_POINT=#{BA_POINT}, SO_ID=#{SO_ID}, IS_STATUS=#{IS_STATUS},
ID=#{ID}

mapper.select:
SELECT BAD.*,BW.BW_USERNAME
FROM(
    SELECT * FROM BUS_ACHIEVEMENT_DETAIL


            AND ID = #{ID}


)BAD
INNER JOIN BUS_WECHAT BW ON BW.ID = BAD.BW_ID

mapper.update:
UPDATE BUS_ACHIEVEMENT_DETAIL
ID=#{ID}, BA_ID=#{BA_ID}, BW_ID=#{BW_ID}, BAD_REMARKS=#{BAD_ADDRESS}, BAD_REMARKS=#{BAD_REMARKS}, BAD_ENTERTIME=#{BAD_ENTERTIME},
ID=#{ID}

mapper.select:
SELECT COUNT(*)
FROM V_ACHIEVEMENT_DETAIL


        AND BW_ID = #{BW_ID}

SELECT *
FROM V_ACHIEVEMENT_DETAIL


        ${whereClause}



    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

SELECT BMIA.*, BA.BA_NAME,SF.ID AS SF_ID,CONCAT(SF.SF_PATH,'@@@',SF.SF_NAME) IMG_PATH FROM( SELECT * FROM BUS_MAIN_IMAGE_ACHIEVEMENT
AND ID = #{ID}
AND BMI_ID = #{BMI_ID}
AND BMI_ID IN(${BMI_ID_LIST})
)BMIA INNER JOIN BUS_ACHIEVEMENT BA ON BA.ID = BMIA.BA_ID LEFT JOIN SYS_FILE SF ON SF.SF_TABLE_NAME = 'BUS_ACHIEVEMENT' AND SF.SF_TABLE_ID = BA.ID

mapper.update:
UPDATE BUS_MAIN_IMAGE_ACHIEVEMENT
ID=#{ID}, BA_ID=#{BA_ID}, BMI_ID=#{BMI_ID},
ID=#{ID}

mapper.delete:
		DELETE FROM BUS_MAIN_IMAGE_ACHIEVEMENT


                AND ID=#{ID}


                AND BA_ID=#{BA_ID}


                AND BMI_ID=#{BMI_ID}
