mapper.select:
SELECT
    BMI.*,
    SF.ID AS SF_ID,CONCAT(SF.SF_PATH,'@@@',SF.SF_NAME) IMG_PATH
FROM(
SELECT * FROM BUS_MAIN_IMAGE


        AND ID = #{ID}


        AND IS_STATUS = #{IS_STATUS}


        AND BMI_PARENTID = #{BMI_PARENTID}


)BMI
LEFT JOIN SYS_FILE SF ON SF.SF_TABLE_NAME = 'BUS_MAIN_IMAGE' AND SF.SF_TABLE_ID = BMI.ID

mapper.update:
UPDATE BUS_MAIN_IMAGE
ID=#{ID}, BMI_PARENTID=#{BMI_PARENTID}, BMI_NAME=#{BMI_NAME}, BMI_HEIGHT=#{BMI_HEIGHT}, BMI_TOP=#{BMI_TOP}, BMI_AREAHEIGHT=#{BMI_AREAHEIGHT}, BMI_AREAWIDTH=#{BMI_AREAWIDTH}, BMI_REMARKS=#{BMI_REMARKS}, BMI_ENTRYTIME=#{BMI_ENTRYTIME}, BMI_UPDATETIME=#{BMI_UPDATETIME}, SO_ID=#{SO_ID}, IS_STATUS=#{IS_STATUS},
ID=#{ID}

mapper.select:
SELECT
    BMIA.*,
    SF.ID AS SF_ID,SF.ID AS SF_ID,CONCAT(SF.SF_PATH,'@@@',SF.SF_NAME) IMG_PATH
FROM(
    SELECT * FROM BUS_MAIN_IMAGE_AREA


            AND ID = #{ID}


            AND BMI_ID = #{BMI_ID}


            AND BMI_RELATIONID = #{BMI_RELATIONID}


)BMIA
LEFT JOIN BUS_MAIN_IMAGE BMI ON BMI.ID = BMIA.BMI_RELATIONID
LEFT JOIN SYS_FILE SF ON SF.SF_TABLE_NAME = 'BUS_MAIN_IMAGE' AND SF.SF_TABLE_ID = BMI.ID

mapper.update:
UPDATE BUS_MAIN_IMAGE_AREA
ID=#{ID}, BMI_ID=#{BMI_ID}, BMI_RELATIONID=#{BMI_RELATIONID}, BIMA_INDEX=#{BIMA_INDEX}, BIMA_TITLE=#{BIMA_TITLE}, BIMA_MAPINFO=#{BIMA_MAPINFO}, BIMA_ENTRYTIME=#{BIMA_ENTRYTIME}, BIMA_UPDATETIME=#{BIMA_UPDATETIME}, SO_ID=#{SO_ID}, IS_STATUS=#{IS_STATUS},
ID=#{ID}
