mapper.select:
SELECT * FROM BUS_WECHAT


        AND ID = #{ID}


        AND BW_OPENID = #{BW_OPENID}

mapper.update:
UPDATE BUS_WECHAT
ID=#{ID}, BW_UUID=#{BW_UUID}, BW_USERNAME=#{BW_USERNAME}, BW_NICKNAME=#{BW_NICKNAME}, BW_GENDER=#{BW_GENDER}, BW_AVATAR=#{BW_AVATAR}, BW_LOCATION=#{BW_LOCATION}, BW_SOURCE=#{BW_SOURCE}, BW_ACCESSTOKEN=#{BW_ACCESSTOKEN}, BW_EXPIREIN=#{BW_EXPIREIN}, BW_OPENID=#{BW_OPENID}, BW_REFRESHTOKEN=#{BW_REFRESHTOKEN}, BW_LOGINTIME=#{BW_LOGINTIME}, IS_STATUS=#{IS_STATUS},
ID=#{ID}

mapper.select:
SELECT * FROM BUS_WECHAT_PRAISE


        AND ID = #{ID}


        AND BW_ID = #{BW_ID}

mapper.update:
UPDATE BUS_WECHAT_PRAISE
ID=#{ID}, BW_ID=#{BW_ID}, BWP_NUMBER=#{BWP_NUMBER},
ID=#{ID}

UPDATE BUS_WECHAT_PRAISE_POINT
ID=#{ID}, BWPP_FROM_ID=#{BWPP_FROM_ID}, BWPP_TO_ID=#{BWPP_TO_ID}, BWPP_ENTRY_TIME=#{BWPP_ENTRY_TIME},
ID=#{ID}

UPDATE BUS_WECHAT_RANK
ID=#{ID}, BW_ID=#{BW_ID}, BW_USERNAME=#{BW_USERNAME}, BW_AVATAR=#{BW_AVATAR}, BWR_RANK=#{BWR_RANK}, BWR_LAST_TIME=#{BWR_LAST_TIME}, BWR_CLOCKIN_COUNT=#{BWR_CLOCKIN_COUNT}, BWP_NUMBER=#{BWP_NUMBER},
ID=#{ID}
